import {
  Dimensions,
  Image,
  StyleSheet,
  View,
  ScrollView,
  Text,
} from 'react-native';
import AmountView from './AmountView';

const DessertDetails = ({route}: {route: any}) => {
  const item = route.params;
  return (
    <View style={styles.mainViewContainer}>
      <ScrollView style={styles.scrollView}>
        <View>
          <Image style={styles.banner} source={item.image} />
          <Text style={[styles.dessertName, styles.itemPadding]}>
            {item.name}
          </Text>

          <View style={[styles.mainAmountView, commonStyle.rowView]}>
            <Text style={[styles.dessertPrice, styles.itemPadding]}>
              {item.price}
            </Text>
            <View style={commonStyle.rowView}>
              <AmountView operation="-" />
              <Text style={styles.amountText}>1</Text>
              <AmountView operation="+" />
            </View>
          </View>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  mainViewContainer: {
    flex: 1,
    backgroundColor: '#fff',
    paddingBottom: 10,
  },
  scrollView: {
    flex: 1,
  },
  banner: {
    width: Dimensions.get('window').width,
    height: Dimensions.get('window').height * 0.5,
    borderBottomLeftRadius: 35,
    borderBottomRightRadius: 35,
    resizeMode: 'cover',
  },
  itemPadding: {
    paddingBottom: 10,
    paddingLeft: 16,
  },
  dessertName: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#5A4682',
    paddingLeft: 30,
    paddingTop: 20,
  },
  dessertPrice: {
    fontSize: 15,
    fontWeight: 'bold',
    color: '#151515',
  },
  mainAmountView: {
    justifyContent: 'space-between',
  },
  amountText: {
    padding: 5,
  },
});
export default DessertDetails;
